#
# TABLE STRUCTURE FOR: media_comment
#

DROP TABLE IF EXISTS `media_comment`;

CREATE TABLE `media_comment` (
  `media_id` bigint(20) unsigned NOT NULL,
  `user_id` bigint(20) unsigned NOT NULL,
  `user_comment` varchar(500) COLLATE utf8_unicode_ci NOT NULL DEFAULT '',
  `create_date` datetime NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  KEY `user_id` (`user_id`),
  KEY `media_id` (`media_id`),
  KEY `create_date` (`create_date`),
  CONSTRAINT `media_comment_ibfk_1` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE CASCADE,
  CONSTRAINT `media_comment_ibfk_2` FOREIGN KEY (`media_id`) REFERENCES `media` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;



